tables.table_schema:
CREATE TABLE session_notes (
  session_id INT PRIMARY KEY,
  therapist_id INT,
  patient_id INT,
  note TEXT
);

CREATE TABLE therapy_sessions (
  session_id INT PRIMARY KEY,
  therapist_name VARCHAR NOT NULL,
  session_date DATE,
  session_notes TEXT
);

CREATE TABLE progress_reports (
  report_id INT PRIMARY KEY,
  session_id INT REFERENCES therapy_sessions(session_id),
  report_date DATE,
  therapist_comments TEXT
);

CREATE TABLE clients (
  client_id INT PRIMARY KEY,
  client_name VARCHAR NOT NULL,
  client_age INT,
  client_gender VARCHAR
);

CREATE TABLE RecordingSessionNotes (
  session_id INT,
  therapist_id INT,
  session_date DATE,
  notes TEXT
);

CREATE TABLE Therapists (
  therapist_id INT,
  therapist_name VARCHAR(50),
  therapist_email VARCHAR(50),
  therapist_phone VARCHAR(15)
);

CREATE TABLE therapists (
  therapist_id INT PRIMARY KEY,
  name VARCHAR(50),
  specialization VARCHAR(50),
  hire_date DATE
);
