tables.table_schema:
CREATE TABLE therapy_clients (
  client_id INT PRIMARY KEY,
  client_name VARCHAR(100) NOT NULL,
  client_email VARCHAR(255) UNIQUE
);

CREATE TABLE therapy_invoices (
  invoice_id INT PRIMARY KEY,
  client_id INT,
  invoice_date DATE,
  amount DECIMAL(10, 2),
  FOREIGN KEY (client_id) REFERENCES therapy_clients(client_id)
);

CREATE TABLE therapy_clients (
    client_id INT PRIMARY KEY,
    name VARCHAR(50),
    email VARCHAR(100),
    phone_number VARCHAR(15)
);

CREATE TABLE therapy_services (
    service_id INT PRIMARY KEY,
    service_name VARCHAR(50),
    service_description TEXT,
    service_rate DECIMAL(10, 2)
);

CREATE TABLE clients (
  client_id INT PRIMARY KEY,
  client_name VARCHAR(255),
  therapist_id INT,
  therapy_type VARCHAR(50)
);

CREATE TABLE therapists (
  therapist_id INT PRIMARY KEY,
  therapist_name VARCHAR(255),
  specialization VARCHAR(100)
);

CREATE TABLE payments (
  payment_id INT PRIMARY KEY,
  invoice_id INT,
  payment_date DATE,
  amount_paid DECIMAL(10, 2)
);

CREATE TABLE Clients (
  client_id INT PRIMARY KEY,
  client_name VARCHAR(50) NOT NULL,
  address VARCHAR(100),
  phone_number VARCHAR(15)
);

CREATE TABLE Therapists (
  therapist_id INT PRIMARY KEY,
  therapist_name VARCHAR(50) NOT NULL,
  specialization VARCHAR(50)
);

CREATE TABLE Services (
  service_id INT PRIMARY KEY,
  service_name VARCHAR(50) NOT NULL,
  description TEXT,
  cost DECIMAL(10, 2) NOT NULL
);
