tables.table_schema:
CREATE TABLE therapists (
  therapist_id UUID PRIMARY KEY,
  therapist_name VARCHAR(255),
  therapist_specialty VARCHAR(100),
  therapist_location VARCHAR(100)
);

CREATE TABLE therapists ( 
 therapist_id INT PRIMARY KEY, 
 therapist_name VARCHAR(50) NOT NULL 
);

CREATE TABLE clients ( 
 client_id INT PRIMARY KEY, 
 client_name VARCHAR(50) NOT NULL, 
 therapist_id INT NOT NULL, 
 CONSTRAINT fk_therapist_id FOREIGN KEY (therapist_id) REFERENCES therapists(therapist_id) 
);

CREATE TABLE resources ( 
 resource_id INT PRIMARY KEY, 
 resource_name VARCHAR(100) NOT NULL, 
 resource_description TEXT 
);
