tables.table_schema:
CREATE TABLE SensorData (
    sensor_id INT PRIMARY KEY,
    timestamp TIMESTAMP,
    sensor_type TEXT,
    value FLOAT
);

CREATE TABLE SensorLocation (
    sensor_id INT PRIMARY KEY,
    location TEXT
);

CREATE TABLE SensorMetadata (
    sensor_id INT PRIMARY KEY,
    manufacturer TEXT,
    model TEXT
);

CREATE TABLE SensorAlerts (
    alert_id INT PRIMARY KEY,
    sensor_id INT,
    timestamp TIMESTAMP,
    message TEXT
);
