tables.table_schema:
CREATE TABLE social_media_interaction (
    id INT PRIMARY KEY,
    user_id INT,
    post_id INT,
    interaction_type VARCHAR(50),
    interaction_date TIMESTAMP
);

CREATE TABLE user_profile (
    user_id INT PRIMARY KEY,
    username VARCHAR(50),
    email VARCHAR(100),
    join_date TIMESTAMP
);

CREATE TABLE social_media_users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    followers_count INT,
    following_count INT
);

CREATE TABLE likes (
    like_id INT PRIMARY KEY,
    post_id INT REFERENCES posts(post_id),
    user_id INT REFERENCES social_media_users(user_id),
    like_date TIMESTAMP
);

CREATE TABLE social_media_users (
    user_id INT PRIMARY KEY,
    user_name TEXT,
    followers_count INT
);

CREATE TABLE hashtags (
    hashtag_id INT PRIMARY KEY,
    hashtag_text TEXT
);

CREATE TABLE post_hashtags (
    post_id INT REFERENCES posts(post_id),
    hashtag_id INT REFERENCES hashtags(hashtag_id)
);
