tables.table_schema:
CREATE TABLE citations (
    citation_id SERIAL PRIMARY KEY,
    paper_id INT REFERENCES research_papers(paper_id),
    cited_paper_id INT REFERENCES research_papers(paper_id),
    citation_text TEXT
);

CREATE TABLE keywords (
    keyword_id SERIAL PRIMARY KEY,
    paper_id INT REFERENCES research_papers(paper_id),
    keyword VARCHAR(50) NOT NULL
);

CREATE TABLE Authors (
    author_id SERIAL PRIMARY KEY,
    author_name VARCHAR(100) NOT NULL,
    affiliation VARCHAR(150),
    email VARCHAR(100) UNIQUE
);

CREATE TABLE Keywords (
    keyword_id SERIAL PRIMARY KEY,
    keyword_text VARCHAR(50) NOT NULL
);

CREATE TABLE PaperKeywords (
    paper_id INT REFERENCES ResearchPapers(id),
    keyword_id INT REFERENCES Keywords(keyword_id),
    PRIMARY KEY (paper_id, keyword_id)
);

CREATE TABLE research_topics (
  topic_id SERIAL PRIMARY KEY,
  topic_name VARCHAR(50) NOT NULL
);

CREATE TABLE paper_keywords (
  id SERIAL PRIMARY KEY,
  paper_id INT REFERENCES research_papers(paper_id),
  keyword VARCHAR(50) NOT NULL
);

CREATE TABLE paper_topics (
  id SERIAL PRIMARY KEY,
  paper_id INT REFERENCES research_papers(paper_id),
  topic_id INT REFERENCES research_topics(topic_id)
);

CREATE TABLE author_details (
  author_id SERIAL PRIMARY KEY,
  author_name VARCHAR(100) NOT NULL,
  affiliation VARCHAR(100)
);
