tables.table_schema:
CREATE TABLE print_logs (
    log_id INT PRIMARY KEY,
    job_id INT,
    log_message TEXT,
    log_timestamp TIMESTAMP
);

CREATE TABLE print_jobs (
    job_id UUID PRIMARY KEY,
    job_name VARCHAR(255) NOT NULL,
    progress INT NOT NULL,
    timestamp TIMESTAMP NOT NULL
);

CREATE TABLE job_history (
    history_id SERIAL PRIMARY KEY,
    job_id UUID REFERENCES print_jobs(job_id),
    status VARCHAR(50) NOT NULL,
    history_timestamp TIMESTAMP NOT NULL
);

CREATE TABLE job_users (
    user_id SERIAL PRIMARY KEY,
    job_id UUID REFERENCES print_jobs(job_id),
    user_name VARCHAR(100) NOT NULL,
    timestamp TIMESTAMP NOT NULL
);

CREATE TABLE job_progress (
    job_id INT PRIMARY KEY,
    progress VARCHAR(255),
    timestamp TIMESTAMP
);

CREATE TABLE job_details (
    job_id INT PRIMARY KEY,
    job_name VARCHAR(255),
    start_time TIMESTAMP,
    end_time TIMESTAMP
);

CREATE TABLE printer_status (
    printer_id INT PRIMARY KEY,
    status VARCHAR(255),
    timestamp TIMESTAMP
);

CREATE TABLE printer_location (
    printer_id INT PRIMARY KEY,
    location VARCHAR(255),
    timestamp TIMESTAMP
);

CREATE TABLE job_printer_mapping (
    job_id INT,
    printer_id INT,
    start_time TIMESTAMP,
    end_time TIMESTAMP
);
