tables.table_schema:
CREATE TABLE DroneBatteryStatus (
    drone_id INT PRIMARY KEY,
    battery_percentage DECIMAL,
    charging_cycles INT
);

CREATE TABLE ChargingStations (
    station_id INT PRIMARY KEY,
    location VARCHAR(255),
    max_capacity INT
);

CREATE TABLE drones (
    drone_id INT PRIMARY KEY,
    battery_level FLOAT NOT NULL,
    charging_cycles INT NOT NULL
);

CREATE TABLE drone_locations (
    location_id INT PRIMARY KEY,
    drone_id INT REFERENCES drones(drone_id),
    latitude FLOAT NOT NULL,
    longitude FLOAT NOT NULL
);

CREATE TABLE battery_replacement (
    replacement_id INT PRIMARY KEY,
    drone_id INT REFERENCES drones(drone_id),
    date_replaced DATE NOT NULL,
    battery_level FLOAT NOT NULL
);

CREATE TABLE charging_stations (
    station_id INT PRIMARY KEY,
    location_id INT REFERENCES drone_locations(location_id),
    charging_capacity FLOAT NOT NULL
);

CREATE TABLE drones (
    drone_id UUID PRIMARY KEY,
    battery_level FLOAT,
    charging_cycles INT,
    last_maintenance_date DATE
);

CREATE TABLE battery_charging_stations (
    station_id UUID PRIMARY KEY,
    location VARCHAR,
    charging_capacity FLOAT,
    power_source VARCHAR
);

CREATE TABLE drone_status (
    drone_id UUID PRIMARY KEY,
    status VARCHAR,
    last_update TIMESTAMP
);

CREATE TABLE power_consumption_logs (
    log_id UUID PRIMARY KEY,
    drone_id UUID,
    time_stamp TIMESTAMP,
    power_consumed FLOAT
);
