tables.table_schema:
CREATE TABLE drone_operators (
  id INT,
  name VARCHAR,
  email VARCHAR,
  operator_license VARCHAR
);

CREATE TABLE maintenance_personnel (
  id INT,
  name VARCHAR,
  email VARCHAR,
  maintenance_license VARCHAR
);

CREATE TABLE UserProfiles (
  id INT PRIMARY KEY,
  user_id INT UNIQUE,
  profile_picture BYTEA,
  bio TEXT
);
