tables.table_schema:
CREATE TABLE WeatherData (
  Date DATE,
  Temperature FLOAT,
  Humidity INT,
  WindSpeed FLOAT
);

CREATE TABLE FlightConditions (
  Date DATE,
  WeatherCondition VARCHAR(50),
  WindSpeedLimit FLOAT,
  TemperatureLimit FLOAT
);

CREATE TABLE Weather_Data (
   Date DATE,
   Temperature FLOAT,
   Humidity FLOAT,
   Wind_Speed FLOAT
);

CREATE TABLE flight_schedules (
    flight_id INT PRIMARY KEY,
    flight_number VARCHAR(20),
    departure_time TIMESTAMP,
    arrival_time TIMESTAMP
);

CREATE TABLE airports (
    airport_id INT PRIMARY KEY,
    airport_code VARCHAR(5),
    airport_name VARCHAR(100),
    city VARCHAR(50)
);

CREATE TABLE weather_data (
 id INT PRIMARY KEY,
 date DATE,
 temperature FLOAT,
 humidity FLOAT
);

CREATE TABLE wind_data (
 id INT PRIMARY KEY,
 date DATE,
 wind_speed FLOAT,
 wind_direction VARCHAR
);

CREATE TABLE airport_data (
 id INT PRIMARY KEY,
 airport_code VARCHAR,
 city VARCHAR,
 country VARCHAR
);
