tables.table_schema:
CREATE TABLE maintenance_records (
    record_id SERIAL PRIMARY KEY,
    drone_id INT NOT NULL,
    maintenance_task VARCHAR(255) NOT NULL,
    maintenance_date DATE NOT NULL
);

CREATE TABLE maintenance_schedule (
    schedule_id SERIAL PRIMARY KEY,
    drone_id INT NOT NULL,
    scheduled_task VARCHAR(255) NOT NULL,
    scheduled_date DATE NOT NULL
);

CREATE TABLE Drone (
  drone_id INT PRIMARY KEY,
  drone_name VARCHAR(255),
  purchase_date DATE,
  last_maintenance_date DATE
);

CREATE TABLE MaintenanceTask (
  task_id INT PRIMARY KEY,
  task_name VARCHAR(255),
  task_description TEXT,
  estimated_duration INT
);

CREATE TABLE IF NOT EXISTS Employees ( 
    id SERIAL PRIMARY KEY, 
    employee_name VARCHAR(100), 
    department VARCHAR(50), 
    hire_date DATE 
);

CREATE TABLE maintenance_records (
    record_id UUID PRIMARY KEY,
    drone_id UUID REFERENCES drones(drone_id),
    maintenance_date DATE,
    maintenance_description TEXT
);

CREATE TABLE maintenance_tasks (
    task_id UUID PRIMARY KEY,
    task_name VARCHAR(50) NOT NULL,
    task_description TEXT
);

CREATE TABLE technicians (
    technician_id UUID PRIMARY KEY,
    technician_name VARCHAR(100) NOT NULL,
    technician_specialization VARCHAR(50)
);
