tables.table_schema:
CREATE TABLE EmployeeSchedules (
    employee_id INT,
    schedule_date DATE,
    start_time TIME,
    end_time TIME
);

CREATE TABLE employee_schedule (
  employee_id INT,
  schedule_date DATE,
  start_time TIME,
  end_time TIME
);

CREATE TABLE task_assignment (
  task_id INT,
  employee_id INT,
  assigned_date DATE,
  completion_status VARCHAR(50)
);

CREATE TABLE employee_details (
  employee_id INT,
  employee_name VARCHAR(100),
  department VARCHAR(50),
  position VARCHAR(50)
);
