tables.table_schema:
CREATE TABLE MotionAnalysisStudies (
    study_id INT PRIMARY KEY,
    study_name VARCHAR(100),
    start_date DATE,
    end_date DATE
);

CREATE TABLE participants (
    id INT,
    name VARCHAR,
    age INT,
    gender VARCHAR
);

CREATE TABLE research_team (
    id INT,
    team_name VARCHAR,
    leader_id INT,
    field_of_study VARCHAR
);
