tables.table_schema:
CREATE TABLE biomechanical_parameters (
  athlete_id INT,
  parameter_name VARCHAR(50),
  parameter_value FLOAT
);

CREATE TABLE performance_analysis (
  athlete_id INT,
  date DATE,
  analysis_result VARCHAR(100)
);

CREATE TABLE Analysis (
  analysis_id INT PRIMARY KEY,
  measurement_id INT,
  performance_id INT,
  result VARCHAR(100)
);
