tables.table_schema:
CREATE TABLE gait_analysis_data (
  participant_id INT PRIMARY KEY,
  stride_length FLOAT,
  cadence INT,
  gait_speed FLOAT
);

CREATE TABLE abnormalities_data (
  abnormality_id INT PRIMARY KEY,
  participant_id INT,
  abnormality_type VARCHAR(50),
  severity_level INT
);

CREATE TABLE walking_patterns (
    pattern_id INT PRIMARY KEY,
    pattern_name VARCHAR(50),
    pattern_description TEXT
);

CREATE TABLE abnormalities (
    abnormality_id INT PRIMARY KEY,
    abnormality_name VARCHAR(50),
    description TEXT
);

CREATE TABLE gait_data_mapping (
    mapping_id INT PRIMARY KEY,
    patient_id INT REFERENCES gait_analysis_data(patient_id),
    pattern_id INT REFERENCES walking_patterns(pattern_id),
    abnormality_id INT REFERENCES abnormalities(abnormality_id)
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username TEXT,
    age INT,
    gender TEXT
);

CREATE TABLE sensors (
    sensor_id INT PRIMARY KEY,
    sensor_name TEXT,
    location TEXT
);

CREATE TABLE walking_patterns (
    pattern_id INT PRIMARY KEY,
    measurement_id INT,
    pattern_description TEXT,
    pattern_score FLOAT
);

CREATE TABLE abnormalities (
    abnormality_id INT PRIMARY KEY,
    measurement_id INT,
    abnormality_type TEXT,
    severity INT
);
