tables.table_schema:
CREATE TABLE bone_density (
    id INT PRIMARY KEY,
    patient_id INT,
    date DATE,
    bone_density FLOAT
);

CREATE TABLE patient (
    id INT PRIMARY KEY,
    name VARCHAR(255),
    age INT,
    gender VARCHAR(10)
);

CREATE TABLE research_data (
    id INT PRIMARY KEY,
    patient_id INT,
    study_date DATE,
    research_notes TEXT
);

CREATE TABLE Patient (
    patient_id INT PRIMARY KEY,
    name VARCHAR(255),
    age INT,
    gender VARCHAR(50)
);

CREATE TABLE BoneDensity (
    record_id INT PRIMARY KEY,
    patient_id INT,
    density DECIMAL,
    measurement_date DATE
    FOREIGN KEY (patient_id) REFERENCES Patient(patient_id)
);

CREATE TABLE ResearchCenter (
    center_id INT PRIMARY KEY,
    name VARCHAR(255),
    location VARCHAR(255)
);

CREATE TABLE ResearchTeam (
    team_id INT PRIMARY KEY,
    center_id INT,
    leader_id INT,
    member_count INT,
    FOREIGN KEY (center_id) REFERENCES ResearchCenter(center_id)
);

CREATE TABLE research_centers (
  center_id SERIAL PRIMARY KEY,
  center_name VARCHAR(100) NOT NULL,
  location VARCHAR(100)
);
