tables.table_schema:
CREATE TABLE Ligaments (
  ligament_id INT PRIMARY KEY,
  ligament_name VARCHAR(50),
  stiffness FLOAT,
  elasticity FLOAT
);

CREATE TABLE Tendons (
  tendon_id INT PRIMARY KEY,
  tendon_name VARCHAR(50),
  strength FLOAT,
  flexibility FLOAT
);

CREATE TABLE ligament_properties (
  ligament_id INT PRIMARY KEY,
  ligament_name VARCHAR(255),
  stiffness FLOAT,
  elasticity FLOAT
);

CREATE TABLE tendon_properties (
  tendon_id INT PRIMARY KEY,
  tendon_name VARCHAR(255),
  tensile_strength FLOAT
);
