tables.table_schema:
CREATE TABLE body_mechanics_impact (
  id INT PRIMARY KEY,
  sport_equipment TEXT NOT NULL,
  impact_description TEXT,
  body_area_affected TEXT
);

CREATE TABLE equipment (
  id UUID PRIMARY KEY,
  name STRING,
  weight DECIMAL,
  impact_factor DECIMAL
);

CREATE TABLE SportsEquipment (
    equipment_id INT PRIMARY KEY,
    equipment_name VARCHAR(50),
    equipment_type VARCHAR(50),
    weight_in_kg DECIMAL
);

CREATE TABLE BodyMechanics (
    mechanics_id INT PRIMARY KEY,
    mechanics_type VARCHAR(50),
    mechanics_description TEXT
);

CREATE TABLE sports_equipment (
  id INT PRIMARY KEY,
  equipment_name VARCHAR(50),
  impact_rating INT
);

CREATE TABLE body_mechanics (
  id INT PRIMARY KEY,
  body_part VARCHAR(50),
  biomechanics_study TEXT
);

CREATE TABLE equipment_impact (
  id INT PRIMARY KEY,
  equipment_id INT,
  body_part_id INT,
  impact_description TEXT
);

CREATE TABLE sport_type (
  id INT PRIMARY KEY,
  sport_name VARCHAR(50),
  equipment_used VARCHAR(100)
);

CREATE TABLE sports_equipment (
	equipment_id INT PRIMARY KEY,
	equipment_name VARCHAR(50) NOT NULL,
	equipment_type VARCHAR(50) NOT NULL
);

CREATE TABLE athletes (
	athlete_id INT PRIMARY KEY,
	athlete_name VARCHAR(50) NOT NULL,
	athlete_age INT,
	athlete_gender VARCHAR(10) NOT NULL
);

CREATE TABLE body_mechanics (
	mechanics_id INT PRIMARY KEY,
	mechanics_type VARCHAR(50) NOT NULL
);

CREATE TABLE sports (
	sport_id INT PRIMARY KEY,
	sport_name VARCHAR(50) NOT NULL
);
