tables.table_schema:
CREATE TABLE Molecular_Structures (
    material_id INT PRIMARY KEY,
    material_name VARCHAR(255) NOT NULL,
    chemical_formula VARCHAR(50) NOT NULL,
    molecular_weight DECIMAL(10,3) NOT NULL
);

CREATE TABLE Analysis_Data (
    analysis_id INT PRIMARY KEY,
    material_id INT NOT NULL,
    analysis_date DATE NOT NULL,
    analysis_result VARCHAR(100) NOT NULL,
    FOREIGN KEY(material_id) REFERENCES Molecular_Structures(material_id)
);

CREATE TABLE Nanomaterials (
  id SERIAL PRIMARY KEY,
  name VARCHAR(255) NOT NULL,
  structure TEXT,
  molecular_weight DOUBLE PRECISION
);

CREATE TABLE Characterization (
  id SERIAL PRIMARY KEY,
  nanomaterial_id INTEGER REFERENCES Nanomaterials(id),
  technique VARCHAR(100) NOT NULL,
  analysis_results TEXT
);

CREATE TABLE Researchers (
  id SERIAL PRIMARY KEY,
  name VARCHAR(100) NOT NULL,
  affiliation VARCHAR(255),
  email VARCHAR(100)
);
