tables.table_schema:
CREATE TABLE efficiency_performance (
    device_id UUID PRIMARY KEY,
    device_name VARCHAR(255),
    efficiency DECIMAL,
    performance DECIMAL
);

CREATE TABLE devices (
    device_id INT PRIMARY KEY,
    device_name VARCHAR(50),
    efficiency DECIMAL,
    performance DECIMAL
);

CREATE TABLE research_facilities (
    facility_id INT PRIMARY KEY,
    facility_name VARCHAR(50),
    location VARCHAR(50)
);

CREATE TABLE EfficiencyMetrics (
    device_id UUID PRIMARY KEY,
    efficiency DECIMAL,
    performance DECIMAL,
    timestamp TIMESTAMP
);

CREATE TABLE ResearchProjects (
    project_id UUID PRIMARY KEY,
    project_name TEXT,
    start_date DATE,
    end_date DATE
);

CREATE TABLE DeviceAssignments (
    assignment_id UUID PRIMARY KEY,
    project_id UUID,
    device_id UUID,
    assigned_date DATE
);

CREATE TABLE efficiency (
	efficiency_id INT PRIMARY KEY,
	device_name TEXT,
	efficiency_score FLOAT
);

CREATE TABLE performance (
	performance_id INT PRIMARY KEY,
	device_name TEXT,
	performance_rating INT
);

CREATE TABLE research (
	research_id INT PRIMARY KEY,
	researcher_name TEXT,
	research_topic TEXT
);

CREATE TABLE nano_devices (
	nano_device_id INT PRIMARY KEY,
	device_name TEXT,
	supplier_name TEXT,
	device_type TEXT
);

CREATE TABLE measurements (
	measurement_id INT PRIMARY KEY,
	device_id INT,
	measurement_date DATE,
	measurement_result FLOAT
);
