tables.table_schema:
CREATE TABLE ExperimentalResults (
  id INT PRIMARY KEY,
  experiment_date DATE,
  researcher_name VARCHAR(50),
  experiment_description TEXT
);

CREATE TABLE NanoscaleSamples (
  sample_id INT PRIMARY KEY,
  sample_name VARCHAR(50),
  sample_material VARCHAR(50),
  sample_size VARCHAR(20)
);

CREATE TABLE ResearcherContacts (
  researcher_id INT PRIMARY KEY,
  researcher_name VARCHAR(50),
  researcher_email VARCHAR(50)
);
