tables.table_schema:
CREATE TABLE quality_control (
    qc_id INT PRIMARY KEY,
    process_id INT,
    parameter_name VARCHAR(255),
    measurement_result FLOAT
);

CREATE TABLE compliance_standards (
    standard_id INT PRIMARY KEY,
    standard_name VARCHAR(255),
    description TEXT
);

CREATE TABLE QualityControlMeasures (
    id UUID PRIMARY KEY,
    manufacturing_date DATE,
    manufacturer VARCHAR(255),
    standard_compliance BOOLEAN
);

CREATE TABLE NanotechnologyStandards (
    id UUID PRIMARY KEY,
    standard_name VARCHAR(255),
    description TEXT,
    regulatory_body VARCHAR(255)
);

CREATE TABLE ProductQualityAssessment (
    id UUID PRIMARY KEY,
    product_id UUID,
    assessment_date DATE,
    passed_quality_test BOOLEAN
);

CREATE TABLE ComplianceLogs (
    id UUID PRIMARY KEY,
    log_date DATE,
    message TEXT
);

CREATE TABLE quality_control_measures (
  qc_id INT PRIMARY KEY,
  qc_name VARCHAR(50),
  qc_description TEXT,
  qc_date TIMESTAMP
);

CREATE TABLE standards (
  standard_id INT PRIMARY KEY,
  standard_name VARCHAR(50),
  standard_description TEXT
);

CREATE TABLE nanotechnology_manufacturing (
  manufacturer_id INT PRIMARY KEY,
  manufacturer_name VARCHAR(50),
  manufacturer_location VARCHAR(100)
);

CREATE TABLE compliance_history (
  history_id INT PRIMARY KEY,
  record_id INT,
  history_details TEXT
);
