tables.table_schema:
CREATE TABLE nanoparticle_samples (
   sample_id INT PRIMARY KEY,
   sample_name VARCHAR(100) NOT NULL,
   sample_size FLOAT NOT NULL,
   classification VARCHAR(50) NOT NULL
);

CREATE TABLE sample_properties (
   property_id INT PRIMARY KEY,
   sample_id INT REFERENCES nanoparticle_samples(sample_id),
   property_name VARCHAR(100) NOT NULL,
   property_value FLOAT NOT NULL
);

CREATE TABLE sample_characteristics (
   characteristic_id INT PRIMARY KEY,
   sample_id INT REFERENCES nanoparticle_samples(sample_id),
   characteristic_name VARCHAR(100) NOT NULL,
   characteristic_value VARCHAR(100) NOT NULL
);

CREATE TABLE nanoparticle_properties (
    id SERIAL PRIMARY KEY,
    name VARCHAR(50) NOT NULL,
    size FLOAT NOT NULL,
    shape VARCHAR(50) NOT NULL,
    surface_area FLOAT NOT NULL
);

CREATE TABLE classification_labels (
    id SERIAL PRIMARY KEY,
    label VARCHAR(50) NOT NULL
);

CREATE TABLE sample_characteristics (
    id SERIAL PRIMARY KEY,
    sample_id INT NOT NULL,
    density FLOAT NOT NULL,
    composition VARCHAR(100) NOT NULL,
    purity FLOAT NOT NULL
);

CREATE TABLE comparison_results (
    id SERIAL PRIMARY KEY,
    sample_id_1 INT NOT NULL,
    sample_id_2 INT NOT NULL,
    similarity_score FLOAT NOT NULL,
    classification_result VARCHAR(50) NOT NULL
);

CREATE TABLE sample_classification (
	sample_id INT PRIMARY KEY,
	classification_type TEXT NOT NULL,
	classification_confidence FLOAT,
	classification_date DATE
);

CREATE TABLE sample_comments (
	comment_id INT PRIMARY KEY,
	sample_id INT NOT NULL,
	comment TEXT,
	comment_date DATE
);
