tables.table_schema:
CREATE TABLE collaborators (
    collaborator_id UUID PRIMARY KEY,
    collaborator_name VARCHAR(255),
    location VARCHAR(255),
    expertise VARCHAR(255)
);

CREATE TABLE researchers (
    researcher_id INT PRIMARY KEY,
    researcher_name VARCHAR(50) NOT NULL,
    researcher_email VARCHAR(50),
    researcher_specialty VARCHAR(50)
);

CREATE TABLE collaborations (
    collaboration_id INT PRIMARY KEY,
    collaboration_name VARCHAR(50) NOT NULL,
    project_id INT,
    collaboration_partner_id INT
);

CREATE TABLE project_updates (
    update_id INT PRIMARY KEY,
    project_id INT,
    update_description TEXT,
    update_date TIMESTAMP
);
