tables.table_schema:
CREATE TABLE inventors (
   inventor_id INT PRIMARY KEY,
   name VARCHAR(255),
   email VARCHAR(255),
   phone_number VARCHAR(15)
);

CREATE TABLE inventors (
    id UUID PRIMARY KEY,
    inventor_name VARCHAR(255) NOT NULL,
    expertise TEXT
);

CREATE TABLE Intellectual_Property_Rights (
   id INT PRIMARY KEY,
   patent_number VARCHAR(50) NOT NULL,
   inventor VARCHAR(100) NOT NULL,
   filing_date DATE NOT NULL
);

CREATE TABLE Nanotechnology_Innovations (
   id INT PRIMARY KEY,
   innovation_name VARCHAR(100) NOT NULL,
   description TEXT NOT NULL,
   application_field VARCHAR(50)
);

CREATE TABLE Patent_Owners (
   id INT PRIMARY KEY,
   patent_number VARCHAR(50) NOT NULL,
   owner_name VARCHAR(100) NOT NULL,
   contact_email VARCHAR(50)
);

CREATE TABLE Patent_Categories (
   id INT PRIMARY KEY,
   category_name VARCHAR(50) NOT NULL
);

CREATE TABLE Legal_Documents (
   id INT PRIMARY KEY,
   document_type VARCHAR(50) NOT NULL,
   document_text TEXT NOT NULL
);
