tables.table_schema:
CREATE TABLE documentation_table (
  document_id SERIAL PRIMARY KEY,
  publication_id INT REFERENCES metadata_table(publication_id),
  document_content TEXT,
  created_at TIMESTAMP
);

CREATE TABLE publication_authors (
    author_id SERIAL PRIMARY KEY,
    author_name TEXT NOT NULL
);

CREATE TABLE publication_keywords (
    keyword_id SERIAL PRIMARY KEY,
    keyword TEXT NOT NULL
);

CREATE TABLE researchers (
    researcher_id UUID PRIMARY KEY,
    name VARCHAR(100),
    affiliation VARCHAR(255),
    research_area VARCHAR(100)
);

CREATE TABLE journals (
    journal_id UUID PRIMARY KEY,
    journal_name VARCHAR(255),
    publisher VARCHAR(255),
    impact_factor FLOAT
);

CREATE TABLE citations (
    citation_id UUID PRIMARY KEY,
    citing_publication_id UUID,
    cited_publication_id UUID,
    citation_date DATE
);

CREATE TABLE keywords (
    keyword_id UUID PRIMARY KEY,
    keyword_name VARCHAR(50)
);
