tables.table_schema:
CREATE TABLE virtual_inventory_items (
    item_id UUID PRIMARY KEY,
    item_name VARCHAR(255) NOT NULL,
    quantity INT NOT NULL
);

CREATE TABLE virtual_inventory_item (
  id INT PRIMARY KEY,
  item_name VARCHAR(255),
  quantity INT,
  price DECIMAL(10, 2)
);

CREATE TABLE user (
  id INT PRIMARY KEY,
  username VARCHAR(255),
  email VARCHAR(255)
);

CREATE TABLE virtual_inventory (
    item_id INT PRIMARY KEY,
    item_name VARCHAR(255),
    quantity INT
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50),
    email VARCHAR(255)
);

CREATE TABLE user_inventory (
    user_id INT,
    item_id INT,
    quantity INT,
    PRIMARY KEY (user_id, item_id)
);

CREATE TABLE Users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50),
    email VARCHAR(100),
    user_type VARCHAR(50)
);
