tables.table_schema:
CREATE TABLE user_preferences (
    user_id INT PRIMARY KEY,
    username VARCHAR(50),
    vr_experience_preferences JSON
);

CREATE TABLE user_settings (
    user_id INT PRIMARY KEY,
    language VARCHAR(20),
    theme VARCHAR(20),
    controller_settings JSON
);

CREATE TABLE UserPreferences (
    user_id UUID,
    display_settings JSON,
    audio_settings JSON
);

CREATE TABLE UserStatistics (
    user_id UUID,
    activities_completed INT,
    favorite_activities JSON,
    last_login TIMESTAMP
);

CREATE TABLE UserPreferences (
    user_id UUID PRIMARY KEY,
    preferred_timezone VARCHAR,
    preferred_language VARCHAR
) ENGINE = 'InnoDB';

CREATE TABLE VRPreferences (
    user_id UUID PRIMARY KEY,
    vr_experience VARCHAR,
    display_settings JSON
) ENGINE = 'InnoDB';

CREATE TABLE UserSettings (
    user_id UUID PRIMARY KEY,
    theme_color VARCHAR,
    font_size INT
) ENGINE = 'InnoDB';

CREATE TABLE VRSettings (
    vr_experience VARCHAR PRIMARY KEY,
    resolution VARCHAR,
    quality INT
) ENGINE = 'InnoDB';

CREATE TABLE UserPreferences (
    id INT PRIMARY KEY,
    user_id INT NOT NULL,
    theme VARCHAR(50) NOT NULL,
    language VARCHAR(50) NOT NULL
);

CREATE TABLE ExperiencePreferences (
    id INT PRIMARY KEY,
    user_id INT NOT NULL,
    experience_id INT NOT NULL,
    rating INT NOT NULL
);

CREATE TABLE DeviceFeatures (
    id INT PRIMARY KEY,
    device_id INT NOT NULL,
    resolution VARCHAR(50) NOT NULL,
    fov VARCHAR(50) NOT NULL
);

CREATE TABLE ThemeSettings (
    id INT PRIMARY KEY,
    user_id INT NOT NULL,
    theme_id INT NOT NULL,
    primary_color VARCHAR(50) NOT NULL
);
