CREATE TABLE permissions (
    permission_id INT PRIMARY KEY,
    permission_name VARCHAR(50) UNIQUE
);

CREATE TABLE user_permissions (
    user_id INT REFERENCES user_profiles(user_id),
    permission_id INT REFERENCES permissions(permission_id),
    PRIMARY KEY (user_id, permission_id)
);

CREATE TABLE permissions (
    permission_id INT PRIMARY KEY,
    permission_name VARCHAR(50) NOT NULL UNIQUE,
    description TEXT
);

CREATE TABLE user_permissions (
    user_id INT REFERENCES users(user_id),
    permission_id INT REFERENCES permissions(permission_id),
    PRIMARY KEY (user_id, permission_id)
);

CREATE TABLE friendships (
    friendship_id UUID PRIMARY KEY,
    user_id_1 UUID REFERENCES users(user_id),
    user_id_2 UUID REFERENCES users(user_id),
    friendship_status VARCHAR(20)
);
