tables.table_schema:
CREATE TABLE VRExperiences (
    experience_id UUID PRIMARY KEY,
    title VARCHAR(255),
    description TEXT,
    release_date DATE
);

CREATE TABLE UserProfiles (
    user_id UUID PRIMARY KEY,
    username VARCHAR(50),
    email VARCHAR(255)
);

CREATE TABLE user_feedback (
    user_id INT PRIMARY KEY,
    username VARCHAR(50),
    feedback TEXT,
    rating INT
);

CREATE TABLE vr_experiences (
    experience_id INT PRIMARY KEY,
    experience_name VARCHAR(50),
    description TEXT
);

CREATE TABLE improvements (
    improvement_id INT PRIMARY KEY,
    experience_id INT,
    suggested_improvements TEXT,
    FOREIGN KEY (experience_id) REFERENCES vr_experiences(experience_id)
);

CREATE TABLE UserFeedback (
	user_id INT,
	vr_experience_id INT,
	feedback TEXT
);

CREATE TABLE VrExperience (
	vr_experience_id INT PRIMARY KEY,
	description TEXT,
	rating INT
);

CREATE TABLE User (
	user_id INT PRIMARY KEY,
	name VARCHAR(50),
	email VARCHAR(100)
);

CREATE TABLE Review (
	review_id INT PRIMARY KEY,
	user_id INT,
	vr_experience_id INT,
	review_text TEXT
);

CREATE TABLE Improvement (
	improvement_id INT PRIMARY KEY,
	vr_experience_id INT,
	description TEXT
);
