tables.table_schema:
CREATE TABLE vehicle_sensor_data (
  sensor_id INT PRIMARY KEY,
  vehicle_id INT,
  gps_coordinates VARCHAR(50),
  speed INT
);

CREATE TABLE VehicleSensorData (
  vehicle_id INT PRIMARY KEY,
  gps_latitude FLOAT,
  gps_longitude FLOAT,
  speed FLOAT
);

CREATE TABLE VehicleHistory (
  vehicle_id INT,
  event_timestamp TIMESTAMP,
  event_description TEXT,
  FOREIGN KEY (vehicle_id) REFERENCES VehicleSensorData(vehicle_id)
);

CREATE TABLE vehicle_gps_data (
    vehicle_id INT,
    gps_coordinates VARCHAR(255),
    speed FLOAT,
    timestamp TIMESTAMP
);

CREATE TABLE vehicle_speed_data (
    vehicle_id INT,
    speed FLOAT,
    timestamp TIMESTAMP
);

CREATE TABLE vehicle_analytics (
    vehicle_id INT,
    route_id INT,
    average_speed FLOAT,
    total_distance FLOAT
);

CREATE TABLE VehicleData (
  vehicle_id INT PRIMARY KEY,
  gps_coordinates VARCHAR(255),
  speed INT
);

CREATE TABLE VehicleEvents (
  event_id INT PRIMARY KEY,
  vehicle_id INT,
  event_type VARCHAR(50),
  event_timestamp TIMESTAMP
);

CREATE TABLE VehicleStatistics (
  stat_id INT PRIMARY KEY,
  vehicle_id INT,
  total_distance INT,
  average_speed INT
);

CREATE TABLE VehicleErrors (
  error_id INT PRIMARY KEY,
  vehicle_id INT,
  error_message TEXT,
  error_timestamp TIMESTAMP
);

CREATE TABLE vehicle_info (
    vehicle_id INT PRIMARY KEY,
    vehicle_type VARCHAR,
    manufacturer VARCHAR,
    model VARCHAR
);

CREATE TABLE vehicle_status (
    vehicle_id INT PRIMARY KEY,
    status VARCHAR,
    last_updated TIMESTAMP
);

CREATE TABLE driver_info (
    driver_id INT PRIMARY KEY,
    name VARCHAR,
    license_number VARCHAR,
    contact_number VARCHAR
);
