tables.table_schema:
CREATE TABLE Drivers (
  driver_id INT PRIMARY KEY,
  driver_name VARCHAR(50) NOT NULL,
  driver_email VARCHAR(100) NOT NULL,
  driver_license VARCHAR(20) NOT NULL
);

CREATE TABLE Preferences (
  pref_id INT PRIMARY KEY,
  pref_name VARCHAR(50) NOT NULL,
  pref_value VARCHAR(50) NOT NULL,
  driver_id INT REFERENCES Drivers(driver_id)
);

CREATE TABLE driver_profile (
	driver_id INT PRIMARY KEY,
	driver_name VARCHAR(255),
	driver_age INT,
	driver_address VARCHAR(255)
);

CREATE TABLE car_preference (
	car_pref_id INT PRIMARY KEY,
	driver_id INT,
	car_type VARCHAR(255),
	music_preference VARCHAR(255)
);

CREATE TABLE music_playlist (
	playlist_id INT PRIMARY KEY,
	playlist_name VARCHAR(255),
	songs_count INT,
	duration_minutes INT
);

CREATE TABLE driver_location (
	location_id INT PRIMARY KEY,
	driver_id INT,
	latitude DECIMAL(9,6),
	longitude DECIMAL(9,6)
);

CREATE TABLE notification_history (
	notification_id INT PRIMARY KEY,
	driver_id INT,
	notification_message TEXT,
	notification_date TIMESTAMP
);
