tables.table_schema:
CREATE TABLE PassengerFeedback (
  feedback_id INT PRIMARY KEY,
  passenger_name VARCHAR(255),
  feedback_text TEXT,
  rating INT
);

CREATE TABLE FeedbackCategories (
  category_id INT PRIMARY KEY,
  category_name VARCHAR(255)
);

CREATE TABLE passenger_feedback (
  feedback_id UUID PRIMARY KEY,
  passenger_id UUID,
  rating INTEGER,
  comment TEXT
);

CREATE TABLE passenger_ratings (
  rating_id UUID PRIMARY KEY,
  passenger_id UUID,
  driver_id UUID,
  rating INTEGER
);

CREATE TABLE passengers (
  passenger_id UUID PRIMARY KEY,
  passenger_name TEXT,
  email TEXT,
  phone_number TEXT
);

CREATE TABLE drivers (
  driver_id UUID PRIMARY KEY,
  driver_name TEXT,
  license_number TEXT,
  vehicle_id UUID
);

CREATE TABLE FeedbackCategory (
    category_id INT PRIMARY KEY,
    category_name VARCHAR
);

CREATE TABLE PassengerFeedbackCategory (
    feedback_id INT,
    category_id INT,
    PRIMARY KEY (feedback_id, category_id)
);

CREATE TABLE RatingScale (
    rating INT PRIMARY KEY,
    description VARCHAR
);
