tables.table_schema:
CREATE TABLE transaction_logs (
  id INT PRIMARY KEY,
  time_stamp TIMESTAMP,
  event_type VARCHAR(50),
  event_details TEXT
);

CREATE TABLE rpa_systems (
    id INT PRIMARY KEY,
    name VARCHAR(255),
    description TEXT,
    created_at TIMESTAMP
);

CREATE TABLE robots (
    id UUID PRIMARY KEY,
    robot_name VARCHAR(50),
    robot_type VARCHAR(50),
    creation_time TIMESTAMP
);

CREATE TABLE users (
    id UUID PRIMARY KEY,
    username VARCHAR(50),
    role VARCHAR(50),
    email VARCHAR(100)
);

CREATE TABLE transaction_logs (
    id INT PRIMARY KEY,
    transaction_data VARCHAR(255),
    timestamp TIMESTAMP
);

CREATE TABLE audit_trails (
    id INT PRIMARY KEY,
    user_id INT,
    action VARCHAR(50),
    timestamp TIMESTAMP
);

CREATE TABLE rpa_task_details (
    id INT PRIMARY KEY,
    task_id INT,
    detail VARCHAR(255)
);

CREATE TABLE compliance_logs (
    id INT PRIMARY KEY,
    log_data VARCHAR(255),
    timestamp TIMESTAMP
);
