CREATE TABLE robots (
	robot_id INT PRIMARY KEY,
	robot_name VARCHAR,
	maintenance_record TEXT,
	software_update_version VARCHAR
);

CREATE TABLE robot_profiles (
    robot_id INT PRIMARY KEY,
    robot_name VARCHAR(255),
    manufacturer VARCHAR(255),
    maintenance_records TEXT
);

CREATE TABLE software_updates (
    update_id INT PRIMARY KEY,
    robot_id INT,
    update_date DATE,
    update_description TEXT
);

CREATE TABLE robot_profiles (
    robot_id UUID PRIMARY KEY,
    robot_name VARCHAR(255) NOT NULL,
    robot_type VARCHAR(50) NOT NULL,
    manufacturer VARCHAR(100) NOT NULL
);

CREATE TABLE maintenance_records (
    record_id SERIAL PRIMARY KEY,
    robot_id UUID NOT NULL,
    maintenance_date DATE NOT NULL,
    maintenance_description TEXT
);

CREATE TABLE software_updates (
    update_id SERIAL PRIMARY KEY,
    robot_id UUID NOT NULL,
    update_date DATE NOT NULL,
    update_description TEXT
);

CREATE TABLE sensors (
    sensor_id SERIAL PRIMARY KEY,
    sensor_name VARCHAR(255) NOT NULL,
    sensor_type VARCHAR(50) NOT NULL,
    robot_id UUID NOT NULL
);

CREATE TABLE tasks (
    task_id SERIAL PRIMARY KEY,
    task_name VARCHAR(255) NOT NULL,
    task_description TEXT,
    robot_id UUID NOT NULL
);
