tables.table_schema:
CREATE TABLE robotic_surgical_systems_usage_statistics (
    system_id INT PRIMARY KEY,
    last_maintenance_date DATE,
    total_usage_hours INT,
    maintenance_frequency INT
);

CREATE TABLE maintenance_schedule (
	schedule_id INT,
	system_id INT,
	scheduled_maintenance_date DATE,
	scheduled_maintenance_type VARCHAR(255)
);

CREATE TABLE robot_models (
    robot_id INT PRIMARY KEY,
    model_name VARCHAR(50),
    manufacturer VARCHAR(50),
    production_year INT
);

CREATE TABLE maintenance_records (
    maintenance_id INT PRIMARY KEY,
    robot_id INT,
    maintenance_date DATE,
    maintenance_type VARCHAR(50)
);

CREATE TABLE maintenance_plans (
    plan_id INT PRIMARY KEY,
    maintenance_type VARCHAR(50),
    description TEXT
);

CREATE TABLE Robot_Models (
  model_id INT PRIMARY KEY,
  model_name VARCHAR(50) NOT NULL,
  manufacturer VARCHAR(50) NOT NULL,
  year_of_manufacture INT
);

CREATE TABLE Surgeon_Statistics (
  surgeon_id INT PRIMARY KEY,
  surgeon_name VARCHAR(50) NOT NULL,
  specialty VARCHAR(50) NOT NULL,
  experience_years INT
);

CREATE TABLE Surgical_Procedures (
  procedure_id INT PRIMARY KEY,
  procedure_name VARCHAR(50) NOT NULL,
  equipment_used VARCHAR(50) NOT NULL,
  complexity_level INT
);

CREATE TABLE Maintenance_Log (
  log_id INT PRIMARY KEY,
  maintenance_date DATE,
  maintenance_type VARCHAR(50) NOT NULL,
  technician_id INT
);
