tables.table_schema:
CREATE TABLE Satellites (
  satellite_id SERIAL PRIMARY KEY,
  name VARCHAR(255) NOT NULL,
  launch_date DATE NOT NULL,
  orbit_height_km INTEGER
);

CREATE TABLE GroundStations (
  station_id SERIAL PRIMARY KEY,
  name VARCHAR(255) NOT NULL,
  country VARCHAR(100) NOT NULL
);

CREATE TABLE satellites (
  satellite_id INT PRIMARY KEY,
  satellite_name VARCHAR(255) NOT NULL,
  launch_date DATE,
  orbit_type VARCHAR(50)
);

CREATE TABLE tracking_stations (
  station_id INT PRIMARY KEY,
  station_name VARCHAR(255) NOT NULL,
  location VARCHAR(100),
  capacity INT
);

CREATE TABLE satellite_status (
  status_id INT PRIMARY KEY,
  satellite_id INT,
  status VARCHAR(50),
  update_time TIMESTAMP
);
