tables.table_schema:
CREATE TABLE Extraterrestrial_Life_Forms (
  id SERIAL PRIMARY KEY,
  name VARCHAR(100) NOT NULL,
  age INT,
  planet VARCHAR(50) NOT NULL
);

CREATE TABLE Alien_Encounters (
  encounter_id SERIAL PRIMARY KEY,
  location VARCHAR(100) NOT NULL,
  date DATE NOT NULL,
  aliens_seen INT
);

CREATE TABLE research_scientists (
    scientist_id INT PRIMARY KEY,
    name VARCHAR(255) NOT NULL,
    specialty VARCHAR(255),
    location VARCHAR(255) NOT NULL
);

CREATE TABLE Extraterrestrial_Life_Forms (
    id INT PRIMARY KEY,
    name VARCHAR(255),
    planet VARCHAR(255),
    description TEXT
);

CREATE TABLE research_centers (
    center_id UUID PRIMARY KEY,
    center_name TEXT NOT NULL,
    location TEXT
);

CREATE TABLE researchers (
    researcher_id UUID PRIMARY KEY,
    researcher_name TEXT NOT NULL,
    field_of_study TEXT,
    center_id UUID REFERENCES research_centers(center_id)
);

CREATE TABLE alien_species (
    species_id UUID PRIMARY KEY,
    species_name TEXT NOT NULL,
    habitat TEXT,
    classification TEXT
);

CREATE TABLE alien_sightings (
    sighting_id UUID PRIMARY KEY,
    sighting_date DATE NOT NULL,
    researcher_id UUID REFERENCES researchers(researcher_id),
    species_id UUID REFERENCES alien_species(species_id)
);

CREATE TABLE research_publications (
    publication_id UUID PRIMARY KEY,
    publication_title TEXT NOT NULL,
    publication_date DATE,
    researcher_id UUID REFERENCES researchers(researcher_id)
);
