tables.table_schema:
CREATE TABLE telemetry_data (telemetry_id UUID PRIMARY KEY,
    mission_id UUID NOT NULL,
    telemetry_timestamp TIMESTAMP NOT NULL,
    telemetry_data JSONB NOT NULL
);

CREATE TABLE spacecraft (spacecraft_id UUID PRIMARY KEY,
    spacecraft_name VARCHAR(100) NOT NULL,
    manufacturer VARCHAR(100),
    launch_date TIMESTAMP
);

CREATE TABLE mission_crew (mission_id UUID,
    astronaut_id UUID,
    role VARCHAR(50) NOT NULL,
    PRIMARY KEY (mission_id, astronaut_id)
);
