tables.table_schema:
CREATE TABLE mission_members (
    member_id SERIAL PRIMARY KEY,
    member_name VARCHAR(100) NOT NULL,
    role VARCHAR(50),
    mission_id INT REFERENCES space_missions(mission_id)
);

CREATE TABLE space_missions (
  mission_id INT PRIMARY KEY,
  mission_name VARCHAR(100) NOT NULL,
  launch_date DATE,
  mission_status VARCHAR(50)
);

CREATE TABLE astronauts (
  astronaut_id INT PRIMARY KEY,
  astronaut_name VARCHAR(100) NOT NULL,
  astronaut_dob DATE,
  mission_id INT
);
