tables.table_schema:
CREATE TABLE mission_control_communication_logs (
  log_id UUID PRIMARY KEY,
  datetime TIMESTAMP,
  message TEXT
);

CREATE TABLE astronaut_communication_logs (
  log_id UUID PRIMARY KEY,
  astronaut_id UUID,
  datetime TIMESTAMP,
  message TEXT
);

CREATE TABLE MissionControlLog (
    log_id INT PRIMARY KEY,
    astronaut_id INT,
    log_timestamp TIMESTAMPTZ,
    log_details TEXT
);

CREATE TABLE SpaceMission (
    mission_id INT PRIMARY KEY,
    mission_name VARCHAR(50),
    launch_date DATE,
    end_date DATE
);

CREATE TABLE mission_control (
   mission_id SERIAL PRIMARY KEY,
   mission_name VARCHAR(50) NOT NULL
);

CREATE TABLE astronauts (
   astronaut_id SERIAL PRIMARY KEY,
   astronaut_name VARCHAR(50) NOT NULL,
   astronaut_role VARCHAR(50) NOT NULL
);

CREATE TABLE message_content (
   message_id SERIAL PRIMARY KEY,
   log_id INT REFERENCES communication_logs(log_id),
   message_text TEXT NOT NULL
);
