);

CREATE TABLE Astronaut ( 
 astronaut_id INT PRIMARY KEY,
 astronaut_name VARCHAR(255) NOT NULL,
 astronaut_age INT,
 mission_id INT 
);

CREATE TABLE missions (
    mission_id SERIAL PRIMARY KEY,
    mission_name TEXT NOT NULL,
    launch_date DATE,
    destination TEXT
);

CREATE TABLE spacecrafts (
    craft_id SERIAL PRIMARY KEY,
    craft_name TEXT NOT NULL,
    manufacturer TEXT,
    launch_mass FLOAT
);

CREATE TABLE explorers (
    explorer_id SERIAL PRIMARY KEY,
    explorer_name TEXT NOT NULL,
    nationality TEXT,
    birth_date DATE
);

CREATE TABLE discoveries (
    discovery_id SERIAL PRIMARY KEY,
    discovery_name TEXT NOT NULL,
    discovered_date DATE,
    mission_id INT REFERENCES missions(mission_id)
);

CREATE TABLE planets (
    planet_id SERIAL PRIMARY KEY,
    planet_name TEXT NOT NULL,
    distance_from_sun FLOAT,
    discovery_id INT REFERENCES discoveries(discovery_id)
);
