tables.table_schema:
CREATE TABLE SpaceStations (
  station_id INT PRIMARY KEY,
  station_name VARCHAR(50) NOT NULL,
  location VARCHAR(100) NOT NULL
);

CREATE TABLE OxygenLevels (
  level_id INT PRIMARY KEY,
  oxygen_level DECIMAL NOT NULL,
  timestamp TIMESTAMP NOT NULL,
  station_id INT REFERENCES SpaceStations(station_id)
);

CREATE TABLE CarbonDioxideLevels (
  level_id INT PRIMARY KEY,
  co2_level DECIMAL NOT NULL,
  timestamp TIMESTAMP NOT NULL,
  station_id INT REFERENCES SpaceStations(station_id)
);
