tables.table_schema:
CREATE TABLE edge_devices_settings (
    device_id UUID PRIMARY KEY,
    setting_name VARCHAR(255) NOT NULL,
    setting_value VARCHAR(255) NOT NULL
);

CREATE TABLE EdgeDevices (
    device_id INT PRIMARY KEY,
    device_name VARCHAR(50) NOT NULL,
    device_location VARCHAR(100) NOT NULL
);

CREATE TABLE DeviceSettings (
    setting_id INT PRIMARY KEY,
    device_id INT NOT NULL,
    setting_name VARCHAR(50) NOT NULL,
    setting_value VARCHAR(255) NOT NULL
);

CREATE TABLE DeviceLogs (
    log_id INT PRIMARY KEY,
    device_id INT NOT NULL,
    log_message TEXT NOT NULL,
    log_timestamp TIMESTAMP NOT NULL
);

CREATE TABLE device_configuration (
    device_id INT PRIMARY KEY,
    device_name VARCHAR(255),
    configuration_settings JSON
);

CREATE TABLE device_type (
    type_id INT PRIMARY KEY,
    type_name VARCHAR(255)
);

CREATE TABLE device_group (
    group_id INT PRIMARY KEY,
    group_name VARCHAR(255)
);

CREATE TABLE configuration_parameters (
    parameter_id INT PRIMARY KEY,
    parameter_name VARCHAR(255),
    default_value VARCHAR(255),
    data_type VARCHAR(50)
);
