tables.table_schema:
CREATE TABLE log_data (
    server_id INT,
    log_timestamp TIMESTAMP,
    log_message STRING
);

CREATE TABLE server_logs (
    server_id INT PRIMARY KEY,
    log_content TEXT,
    log_timestamp TIMESTAMP
);

CREATE TABLE server_logs (
    server_name STRING,
    log_timestamp TIMESTAMP,
    log_message STRING
);

CREATE TABLE server_metrics (
    server_name STRING,
    metric_timestamp TIMESTAMP,
    cpu_usage DECIMAL,
    memory_usage DECIMAL
);

CREATE TABLE server_performance (
    server_name STRING,
    performance_timestamp TIMESTAMP,
    response_time DECIMAL,
    throughput DECIMAL
);

CREATE TABLE servers_logs (
    log_id INT PRIMARY KEY,
    server_id INT,
    log_date DATE,
    log_message TEXT
);

CREATE TABLE servers (
    server_id INT PRIMARY KEY,
    server_name TEXT,
    server_ip VARCHAR(15),
    server_location TEXT
);

CREATE TABLE servers (
    id INT PRIMARY KEY,
    server_name VARCHAR(50),
    ip_address VARCHAR(15),
    location VARCHAR(50)
);

CREATE TABLE logs (
    log_id INT PRIMARY KEY,
    server_id INT,
    log_timestamp TIMESTAMP,
    log_message TEXT
);

CREATE TABLE errors (
    error_id INT PRIMARY KEY,
    server_id INT,
    error_timestamp TIMESTAMP,
    error_message TEXT
);

CREATE TABLE alerts (
    alert_id INT PRIMARY KEY,
    server_id INT,
    alert_timestamp TIMESTAMP,
    alert_message TEXT
);
