tables.table_schema:
CREATE TABLE sensor_readings (
    id INT PRIMARY KEY,
    sensor_id INT NOT NULL,
    reading_value FLOAT NOT NULL,
    timestamp TIMESTAMP NOT NULL
);

CREATE TABLE sensor_reading (
  sensor_id INT,
  sensor_type VARCHAR(50),
  reading_value FLOAT,
  timestamp TIMESTAMP
);

CREATE TABLE machinery (
  machinery_id INT,
  machinery_name VARCHAR(100),
  location VARCHAR(100),
  status VARCHAR(20)
);

CREATE TABLE alerts (
  alert_id INT,
  sensor_id INT,
  alert_message VARCHAR(200),
  timestamp TIMESTAMP
);

CREATE TABLE maintenance_log (
  log_id INT,
  machinery_id INT,
  maintenance_description VARCHAR(200),
  timestamp TIMESTAMP
);
