tables.table_schema:
CREATE TABLE social_media_interactions (
    interaction_id INT PRIMARY KEY,
    user_id INT,
    post_text TEXT,
    sentiment_score FLOAT
);

CREATE TABLE customer_feedback (
    feedback_id INT PRIMARY KEY,
    user_id INT,
    feedback_text TEXT,
    rating INT
);

CREATE TABLE social_media_users (
    user_id INT PRIMARY KEY,
    username TEXT,
    follower_count INT
);

CREATE TABLE posts (
    post_id INT PRIMARY KEY,
    user_id INT,
    post_text TEXT,
    timestamp TIMESTAMP
);

CREATE TABLE comments (
    comment_id INT PRIMARY KEY,
    post_id INT,
    user_id INT,
    comment_text TEXT
);

CREATE TABLE reactions (
    reaction_id INT PRIMARY KEY,
    post_id INT,
    user_id INT,
    reaction_type VARCHAR
);
