tables.table_schema:
CREATE TABLE locations (
  location_id INT PRIMARY KEY,
  location_name VARCHAR(255),
  latitude DECIMAL,
  longitude DECIMAL
);

CREATE TABLE geospatial_data (
  id INT PRIMARY KEY,
  latitude DECIMAL,
  longitude DECIMAL,
  location_name TEXT
);

CREATE TABLE route_optimization (
  route_id INT PRIMARY KEY,
  start_location TEXT,
  end_location TEXT,
  distance DECIMAL
);

CREATE TABLE location_services (
  service_id INT PRIMARY KEY,
  service_name TEXT,
  description TEXT
);

CREATE TABLE location_history (
  history_id INT PRIMARY KEY,
  location_name TEXT,
  time_stamp TIMESTAMP,
  user_id INT
);
