tables.table_schema:
CREATE TABLE machine_learning_models (
    model_id UUID PRIMARY KEY,
    model_name STRING,
    model_type STRING,
    model_accuracy FLOAT
);

CREATE TABLE edge_devices (
    device_id UUID PRIMARY KEY,
    device_name STRING,
    device_os STRING,
    device_location STRING
);

CREATE TABLE edge_devices (
    device_id INT PRIMARY KEY,
    device_name VARCHAR(50) NOT NULL,
    device_location VARCHAR(100),
    device_details JSON
);

CREATE TABLE MachineLearningModels (
    id INT PRIMARY KEY,
    model_name VARCHAR(255) NOT NULL,
    model_type VARCHAR(50) NOT NULL,
    accuracy DOUBLE NOT NULL
);

CREATE TABLE ModelHyperparameters (
    id INT PRIMARY KEY,
    model_id INT NOT NULL,
    hyperparameter_name VARCHAR(50) NOT NULL,
    hyperparameter_value DOUBLE NOT NULL
);

CREATE TABLE EdgeDevices (
    id INT PRIMARY KEY,
    device_name VARCHAR(100) NOT NULL,
    device_type VARCHAR(50) NOT NULL,
    device_location VARCHAR(255) NOT NULL
);

CREATE TABLE users (
    user_id UUID PRIMARY KEY,
    username VARCHAR(50) UNIQUE,
    email VARCHAR(100),
    password_hash VARCHAR(255)
);

CREATE TABLE devices (
    device_id UUID PRIMARY KEY,
    device_name VARCHAR(50),
    location VARCHAR(100),
    user_id UUID REFERENCES users(user_id)
);

CREATE TABLE training_data (
    data_id UUID PRIMARY KEY,
    input_data JSON,
    output_data JSON,
    model_id UUID REFERENCES models(model_id)
);
