CREATE TABLE annotations (
    annotation_id SERIAL PRIMARY KEY,
    image_id INT NOT NULL,
    annotation_text TEXT NOT NULL,
    FOREIGN KEY (image_id) REFERENCES images(image_id)
);

CREATE TABLE models (
    model_id SERIAL PRIMARY KEY,
    model_name TEXT NOT NULL,
    model_description TEXT
);

CREATE TABLE images (
    image_id UUID PRIMARY KEY,
    image_data BYTEA,
    label VARCHAR(50)
);

CREATE TABLE labels (
    label_id UUID PRIMARY KEY,
    label_name VARCHAR(50) UNIQUE
);

CREATE TABLE image_labels (
    image_id UUID REFERENCES images(image_id),
    label_id UUID REFERENCES labels(label_id),
    PRIMARY KEY (image_id, label_id)
);

CREATE TABLE image_metadata (
    image_id UUID REFERENCES images(image_id),
    metadata_key VARCHAR(50),
    metadata_value TEXT,
    PRIMARY KEY (image_id, metadata_key)
);

CREATE TABLE Images (
    image_id INT PRIMARY KEY,
    image_url VARCHAR(255) NOT NULL,
    label VARCHAR(50) NOT NULL
);

CREATE TABLE ImageLabels (
    label_id INT PRIMARY KEY,
    label_name VARCHAR(50) UNIQUE NOT NULL
);

CREATE TABLE Models (
    model_id INT PRIMARY KEY,
    model_name VARCHAR(50) UNIQUE NOT NULL
);
